package com.tcgr.wx.core;import android.os.AsyncTask;import com.tcgr.wx.core.interfaces.Constants;import java.io.IOException;import java.net.Proxy;import org.ksoap2.SoapEnvelope;import org.ksoap2.serialization.SoapObject;import org.ksoap2.serialization.SoapPrimitive;import org.ksoap2.serialization.SoapSerializationEnvelope;import org.ksoap2.transport.HttpTransportSE;import org.xmlpull.v1.XmlPullParserException;/** * SOAP Request - Rocket Route WebSite * * Created by thomas on 13/03/16. */public abstract class AbstractNotamSoapRequest extends AsyncTask<String, Void, String> {    @Override    protected String doInBackground(String... params) {        SoapObject request = new SoapObject(Constants.NAMESPACE, Constants.METHOD_NAME);        request.setInnerText(String.format(Constants.RR_REQUEST, params[0]));        SoapSerializationEnvelope envelope = new SoapSerializationEnvelope(SoapEnvelope.VER11);        envelope.dotNet = true;        envelope.implicitTypes = true;        envelope.setAddAdornments(false);        envelope.setOutputSoapObject(request);        HttpTransportSE ht = new HttpTransportSE(Proxy.NO_PROXY, Constants.MAIN_REQUEST_URL, 60000);        ht.debug = false;        ht.setXmlVersionTag("<!--?xml version=\"1.0\" encoding= \"UTF-8\" ?-->");        try {            ht.call(Constants.SOAP_ACTION, envelope);        } catch (IOException | XmlPullParserException e) {            e.printStackTrace();        }        SoapPrimitive s = (SoapPrimitive) envelope.bodyIn;        return s.getValue().toString();    }}