package com.tcgr.wx.core;import com.loopj.android.http.AsyncHttpClient;import com.loopj.android.http.AsyncHttpResponseHandler;/** * Async Client from LoopJ * Created by thomas on 25/11/15. */public class Async {    private static Async instance;    private static AsyncHttpClient client;    private Async() {        client = new AsyncHttpClient();    }    public static void get(String url, AsyncHttpResponseHandler responseHandler) {        if (instance == null) {            instance = new Async();            client.setMaxConnections(3);            client.setTimeout(20 * 1000);            client.setConnectTimeout(20 * 1000);            client.setResponseTimeout(20 * 1000);        }        De.bug("@Async", url);        client.get(url, null, responseHandler);    }    public static AsyncHttpClient getClient() {        return client;    }}