package com.tcgr.wx.core.preference;import android.content.Context;import android.content.DialogInterface;import android.support.v7.app.AlertDialog;import android.support.v7.preference.DialogPreference;import android.util.AttributeSet;import android.widget.Toast;import com.tcgr.wx.R;import com.tcgr.wx.core.db.DbSchema;/** * * Created by thomas on 17/05/16. */public class ConfirmDialogPreference extends DialogPreference {    public ConfirmDialogPreference(Context context, AttributeSet attrs) {        super(context, attrs);    }    @Override    protected void onClick() {        AlertDialog.Builder dialog = new AlertDialog.Builder(getContext());        dialog.setTitle(getContext().getString(R.string.reset_app));        dialog.setMessage(getContext().getString(R.string.reset_metar_taf));        dialog.setCancelable(true);        dialog.setPositiveButton(getContext().getString(R.string.sim), new DialogInterface.OnClickListener() {            @Override            public void onClick(DialogInterface dialog, int which) {                getContext().getContentResolver().delete(DbSchema.MetarSearchTable.CONTENT_URI, null, null);                getContext().getContentResolver().delete(DbSchema.TafSearchTable.CONTENT_URI, null, null);                Toast.makeText(getContext(), getContext().getString(R.string.sucesso), Toast.LENGTH_SHORT).show();            }        });        dialog.setNegativeButton(getContext().getString(R.string.nao), new DialogInterface.OnClickListener() {            @Override            public void onClick(DialogInterface dialog, int which) {                dialog.dismiss();            }        });        AlertDialog alertDialog = dialog.create();        alertDialog.show();    }}